    stages {
        stage ('Removing Docker Images') {
            steps {
                script {
                    build(job: "enm-cn-docker-purge-slaves-3B", propagate: true, wait: true)
					build(job: "enm-cn-docker-purge-slaves-5A", propagate: true, wait: true)
                }
            }
        }
        stage ('Initialization of ENM Versions') {
            steps {
                script {
                    echo "Triggering get ENM Versions.... "
                }
            }
        }
        stage ('Building OS Base Images') {
            parallel {
                stage ('Build eric-enm-rhel6base') {
                    steps {
                        script {
                            echo "Triggering Base images .... and so on ...."
                        }
                    }
                }
            }
        }
    }
    post {
        success {
            script {
                echo "Success"
            }
        }
        failure {
            script {
                echo "Failure"
            }
        }
    }
}
