javaposse.jobdsl.plugin.ExecuteDslScript.scriptText:
import java.io.File

if( Jobs_required == "Yes") {
  createPipelineBuildJob(RELEASE_JOB)
  queue(RELEASE_JOB)
  createPipelineBuildJobPrecommit(PRECOMMIT_JOB)
  //queue(PRECOMMIT_JOB) - we are executing after review link creation
}

def createPipelineBuildJob(project_name) {
    jenkinsFile_path = "Jenkinsfile"
    pipeline_name = project_name

    println "************* Creating Release JOB " + pipeline_name + " *************"
    pipelineJob(pipeline_name) {
        description ('ENM Cloud Native Build pipeline ' + pipeline_name + ' - autogenerated using JobDSL - all manual changes will be overwritten!')
        properties {
        	disableConcurrentBuilds()
    	}        
        parameters {
            stringParam('ISO_VERSION')
        }
        logRotator {
            numToKeep(30)
        }
        definition {
            cpsScm {
                scm {
                    git {
                        branch('master')
                        remote {
                            credentials('enmadm100_private_key')
                            url("${GERRIT_MIRROR}/" + REPO)
                        }
                        extensions {
                             cleanBeforeCheckout()
                        }
                    }
                }
                scriptPath(jenkinsFile_path)
            }
        }
   }
}

def createPipelineBuildJobPrecommit(project_name) {
	jenkinsfile_path = 'JenkinsfilePreCommit'
    pipeline_name_precommit = project_name
	def repo_url = "ssh://enmadm100@gerritmirror-ha.lmera.ericsson.se:29418/" + REPO

    println "************* Creating PreCommit JOB " + pipeline_name_precommit + " *************"
    pipelineJob(pipeline_name_precommit) {
      description ('ENM Cloud Native precommit pipeline ' + pipeline_name_precommit + ' - autogenerated using JobDSL - all manual changes will be overwritten!')
      properties {
          disableConcurrentBuilds()
      }       
      triggers {
          gerrit {
              events {
                  patchsetCreated()
              }
              repo_name = repo_url.split("ssh://enmadm100@gerritmirror-ha.lmera.ericsson.se:29418/").last()
              project(repo_name, ['plain:master'])
          }
      }
        definition {
            cpsScm {
                scm {
                    git {
                        branch('$GERRIT_REFSPEC')
                        remote {
                            credentials('enmadm100_private_key')
                            url(repo_url)
                            refspec('$GERRIT_REFSPEC:$GERRIT_REFSPEC')
                        }
                    }
                }
            scriptPath(jenkinsfile_path)
            }
    	}
	}
}

hudson.tasks.Shell.command:
#!/bin/bash
if [[ "${REPO_TYPE}" == "Empty" ]]; then exit 0 fi CI_REPO="OSS/com.ericsson.oss.containerisation/enm-containerisation-ci-pipeline" PROJECT_NAME=${CI_REPO##*/} PROJECT_NAME1=${REPO_NAME##*/} myWorkspace=$(pwd)
ISO_VERSION='0.0.0' echo pipeline_name1="${pipeline_name}" >> $myWorkspace/env.properties echo pipeline_name2="${pipeline_name_precommit}" >> $myWorkspace/env.properties echo ISO_VERSION="${ISO_VERSION}" >> $myWorkspace/env.properties GERRIT_MIRROR_HOST=$(echo ${GERRIT_MIRROR} | sed -e 's/ssh:\/\/\(.*\):29418/\1/') rm -rf ${PROJECT_NAME} git clone ${GERRIT_MIRROR}/${CI_REPO} && scp -p -P 29418 ${GERRIT_MIRROR_HOST}:hooks/commit-msg ${PROJECT_NAME}/.git/hooks/
cd ${PROJECT_NAME}
if [[ "${REPO_TYPE}" == "Service_Group" ]]; then COMMIT_MESSAGE="${JIRA_TICKET}: add new Service Group repo" echo "${REPO_NAME}" >> jenkins/projects_service_group else COMMIT_MESSAGE="${JIRA_TICKET}: add new Base Image repo" echo "${REPO_NAME}" >> jenkins/projects_common fi
git add --all git commit -m "${COMMIT_MESSAGE}" git remote set-url origin --push ${GERRIT_CENTRAL}/${CI_REPO}
git push origin HEAD:refs/for/master > review_message.txt 2>&1 cat review_message.txt
REVIEW_DETAILS=$(grep -F "${COMMIT_MESSAGE}" review_message.txt | tr -s " ") REVIEW_LINK=$(cut -d' ' -f2 <<<"${REVIEW_DETAILS}")
echo REVIEW_DATE=$(date "+%d-%m-%Y") >> $myWorkspace/env.properties echo REVIEW_LINK=${REVIEW_LINK} >> $myWorkspace/env.properties
git clone ${GERRIT_MIRROR}/${REPO_NAME} && scp -p -P 29418 ${GERRIT_MIRROR_HOST}:hooks/commit-msg ${PROJECT_NAME1}/.git/hooks/ cd ${PROJECT_NAME1}
touch testfile echo "testing" > testfile
git add testfile git commit -m "testing" git remote set-url origin --push ${GERRIT_CENTRAL}/${REPO_NAME} git push origin HEAD:refs/for/master

javaposse.jobdsl.plugin.ExecuteDslScript.scriptText:
import java.io.File

if( Jobs_required == "Yes" && "${REPO_TYPE}" != "Empty") {
  /*
  def jobRelease = jenkins.model.Jenkins.instance.getItemByFullName("${RELEASE_JOB}")
  println jobRelease.getLastBuild()
  println "${RELEASE_JOB}" + ":" + jobRelease.getLastBuild().getResult().toString()


  def jobPreCommit = jenkins.model.Jenkins.instance.getItemByFullName("${PRECOMMIT_JOB}")
  println jobPreCommit.getLastBuild()
  println "${PRECOMMIT_JOB}" + ":" + jobPreCommit.getLastBuild().getResult().toString()
  */

  //Release Job Status
  try {

        def jobRelease = jenkins.model.Jenkins.instance.getItemByFullName("${RELEASE_JOB}")
        println 'Job: ' + "${RELEASE_JOB}"

    	def releaseJobStatus = jobRelease.isBuilding()
        println 'IsBuilding: ' + "${releaseJobStatus}"

        while (releaseJobStatus) {
            releaseJobStatus = jobRelease.isBuilding()
        } 

        if (jobRelease.getLastBuild()) {
            last_job_num = jobRelease.getLastBuild()
            println 'LastBuildNumer: ' + last_job_num
            println "Status" + ": " + jobRelease.getLastBuild().getResult().toString()
        } 
        else {
            println 'LastBuildNumer: Null'
        }
  } 
  catch (Exception e) {
        println ' Ignoring exception ' + e
  }

  //Precommit Job Status
  try {
        def jobPreCommit = jenkins.model.Jenkins.instance.getItemByFullName("${PRECOMMIT_JOB}")
        println 'Job: ' + "${PRECOMMIT_JOB}"

        def preCommitJobStatus = jobPreCommit.isBuilding()
        println 'IsBuilding: ' + "${preCommitJobStatus}"

        while (preCommitJobStatus) {
            preCommitJobStatus = jobPreCommit.isBuilding()
        }

        if (jobPreCommit.getLastBuild()) {
            last_job_num = jobPreCommit.getLastBuild()
            println 'LastBuildNumer: ' + last_job_num
            println "Status" + ": " + jobPreCommit.getLastBuild().getResult().toString()
        } 
        else {
            println 'LastBuildNumer: Null'
        }
  } 
  catch (Exception e) {
        println ' Ignoring exception ' + e
  }

}
