definition.script:
#!/usr/bin/env groovy

/* IMPORTANT:
 *
 * In order to make this pipeline work, the following configuration on Jenkins is required:
 * - slave with a specific label (see pipeline.agent.label below)
 * - credentials plugin should be installed and have the secrets with the following names:
 *   + lciadm100credentials (token to access Artifactory)
 */

def defaultBobImage = 'armdocker.rnd.ericsson.se/proj-am/releases/eric-am-package-manager:2.0.28'
def metadataImage = 'armdocker.rnd.ericsson.se/proj_oss_releases/enm/cenm-build-utilities:latest'

def bob = new BobCommand()
        .bobImage(defaultBobImage)
        .needDockerSocket(true)
        .toString()

def GIT_COMMITTER_NAME = 'lciadm100'
def GIT_COMMITTER_EMAIL = 'lciadm100@ericsson.com'
def failedStage = ''
def csar_package_name = 'enm_csar_package'



pipeline {
    agent {
        label 'Jenkins_Upgrade_CSAR_Slave_4Node_MISTY_21'
    }
    environment{
        repositoryUrl = "https://arm902-eiffel004.athtem.eei.ericsson.se:8443/nexus/content/repositories/releases/"
        CSAR_PACKAGE_NAME = "enm-installation-package"
        BRO_CLI_IMAGE = "backup-restore-cli:latest"
        BRO_CLI_IMAGE_PATH = "armdocker.rnd.ericsson.se/proj-enm"
    }

    stages {
       stage ('Clean workspace') {
          steps {
                script {
                          sh "docker image prune -fa"
               }
            deleteDir()
         }
       }
       stage('Checkout Integration Pipeline Git Repository') {
            steps {
                git branch: 'master',
                        url: '${GERRIT_MIRROR}/OSS/com.ericsson.oss.containerisation/eric-enm-csar-package'
                sh '''
                    git remote set-url origin --push ${GERRIT_CENTRAL}/OSS/com.ericsson.oss.containerisation/eric-enm-csar-package
                '''
         }
        }
        stage( 'Pre Configurations' ) {
            steps {
                 script{
                        sh 'mkdir -p ${PWD}/charts'
                    }
            }
        }
        stage('Get Integration Chart'){
                steps{
                     sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-bro-integration/eric-enm-bro-integration-${BRO_CHART_VERSION}.tgz -o charts/eric-enm-bro-integration-${BRO_CHART_VERSION}.tgz'
                     sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-pre-deploy-integration/eric-enm-pre-deploy-integration-${PRE_DEPLOY_CHART_VERSION}.tgz -o charts/eric-enm-pre-deploy-integration-${PRE_DEPLOY_CHART_VERSION}.tgz'
                     sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-monitoring-integration/eric-enm-monitoring-integration-${MONITORING_CHART_VERSION}.tgz -o charts/eric-enm-monitoring-integration-${MONITORING_CHART_VERSION}.tgz'
                     sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-infra-integration/eric-enm-infra-integration-${INFRA_CHART_VERSION}.tgz -o charts/eric-enm-infra-integration-${INFRA_CHART_VERSION}.tgz'
                     sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-stateless-integration/eric-enm-stateless-integration-${STATELESS_CHART_VERSION}.tgz -o charts/eric-enm-stateless-integration-${STATELESS_CHART_VERSION}.tgz'
            }
        }
        stage('integration value version'){
            steps{
                script {
                     sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-production-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-production-values-${INTEGRATION_VALUE_VERSION}.yaml'
                     sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-extra-large-production-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-extra-large-production-values-${INTEGRATION_VALUE_VERSION}.yaml'
					 sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-functional-kaas-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-functional-kaas-values-${INTEGRATION_VALUE_VERSION}.yaml'
					 sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-functional-test-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-functional-test-values-${INTEGRATION_VALUE_VERSION}.yaml'
					 sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-kaas-core-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-kaas-core-values-${INTEGRATION_VALUE_VERSION}.yaml'
					 sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-openstack-core-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-openstack-core-values-${INTEGRATION_VALUE_VERSION}.yaml'
					 sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-multi-instance-functional-integration-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-multi-instance-functional-integration-values-${INTEGRATION_VALUE_VERSION}.yaml'
					 sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-single-instance-production-integration-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-single-instance-production-integration-values-${INTEGRATION_VALUE_VERSION}.yaml'
					 }
                }
        }	
        stage('Retrieve version prefix') {
            steps {
                script {
                    env.VERSION_PREFIX = sh(script: "cat VERSION_PREFIX", returnStdout:true).trim()
                    echo "${VERSION_PREFIX}"
                }
            }
        }
        stage('Update manifest details') {
            steps {
                script {
                    env.NOW = sh(script: "date +'%FT%TZ'", returnStdout:true).trim()
                    echo "${NOW}"
                    sh "sed -i -e '/^vnf_package_version:/s/.*/vnf_package_version: $VERSION_PREFIX/' -e '/^vnf_release_date_time:/s/.*/vnf_release_date_time: $NOW/' manifest/enm_descriptor.mf"
