                sh '''
                    git remote set-url origin --push ${GERRIT_CENTRAL}/OSS/com.ericsson.oss.containerisation/eric-enm-csar-package
                '''
         }
        }
        stage( 'Pre Configurations' ) {
            steps {
                 script{
                        sh 'mkdir -p ${PWD}/charts'
                        sh 'rm -rf ${PWD}/scripts/*'
                        sh 'cp settings.xml ${HOME}/.m2/'
						sh(script: 'wget -q -O - --no-check-certificate --post-data="" https://ci-portal.seli.wh.rnd.internal.ericsson.com/api/manageCNProductSet/\${PRODUCT_SET}/cENM-Build-CSAR-LITE/in_progress/', returnStdout: true).trim()
                        echo "here,status of job has to be updated to ci portal as in_progress"
                        env.VNF_RELEASE_DATE_TIME = sh(script: "date +'%FT%TZ'", returnStdout: true).trim()
                        echo "${VNF_RELEASE_DATE_TIME}"
                        env.ENM_RSTATE_NUMBER = sh(script: 'wget -q -O - --no-check-certificate "https://ci-portal.seli.wh.rnd.internal.ericsson.com/getAOMRstate/?product=ENM&drop=${SPRINT_TAG}"', returnStdout: true).trim()
                        echo "${ENM_RSTATE_NUMBER}"
                    }
            }
        }
        stage('Get Integration Chart'){
                steps{
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-bro-integration/eric-enm-bro-integration-${BRO_CHART_VERSION}.tgz -o charts/eric-enm-bro-integration-${BRO_CHART_VERSION}.tgz'
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-pre-deploy-integration/eric-enm-pre-deploy-integration-${PRE_DEPLOY_CHART_VERSION}.tgz -o charts/eric-enm-pre-deploy-integration-${PRE_DEPLOY_CHART_VERSION}.tgz'
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-monitoring-integration/eric-enm-monitoring-integration-${MONITORING_CHART_VERSION}.tgz -o charts/eric-enm-monitoring-integration-${MONITORING_CHART_VERSION}.tgz'
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-infra-integration/eric-enm-infra-integration-${INFRA_CHART_VERSION}.tgz -o charts/eric-enm-infra-integration-${INFRA_CHART_VERSION}.tgz'
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-stateless-integration/eric-enm-stateless-integration-${STATELESS_CHART_VERSION}.tgz -o charts/eric-enm-stateless-integration-${STATELESS_CHART_VERSION}.tgz'
            }
        }
        stage('integration value version'){
            steps{
                script {
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-production-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-production-values-${INTEGRATION_VALUE_VERSION}.yaml'
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-extra-large-production-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-extra-large-production-values-${INTEGRATION_VALUE_VERSION}.yaml'
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-functional-kaas-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-functional-kaas-values-${INTEGRATION_VALUE_VERSION}.yaml'
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-functional-test-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-functional-test-values-${INTEGRATION_VALUE_VERSION}.yaml'
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-kaas-core-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-kaas-core-values-${INTEGRATION_VALUE_VERSION}.yaml'
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-integration-openstack-core-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-openstack-core-values-${INTEGRATION_VALUE_VERSION}.yaml'
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-helm/eric-enm-integration-values/eric-enm-multi-instance-functional-integration-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-multi-instance-functional-integration-values-${INTEGRATION_VALUE_VERSION}.yaml'
