hudson.tasks.Shell.command:
#!/bin/sh
echo "Retrieving Scripts from Nexus" tarFileName="utils_${mt_utils_version}.tar.gz" echo "Downloading file - ${tarFileName} - to the workspace" curl -s --noproxy \* -L "https://arm1s11-eiffel004.eiffel.gic.ericsson.se:8443/nexus/service/local/artifact/maven/redirect?r=releases&g=com.ericsson.mtg&a=utils&p=tar.gz&v=${mt_utils_version}" -o ${tarFileName} tar -zxf ${tarFileName}
echo "tafVersion=${taf_version}" >> "${WORKSPACE}/parameters.properties" echo "centralCSVVersion=${central_csv_version}" >> "${WORKSPACE}/parameters.properties" echo "tafSchedulerVersion=${taf_scheduler_version}" >> "${WORKSPACE}/parameters.properties" echo "conf_level_for_this_job=RFA" >> "${WORKSPACE}/parameters.properties"
sh MTELoopScripts/pipeline_scripts/rfa_250_setup.sh "${WORKSPACE}/parameters.properties" || exit 1
