definition.script:
SG_PROJECT_LIST = ["eric-enmsg-access-control", "eric-enmsg-amos", "eric-enmsg-auto-id-solr", "eric-enmsg-autoid-service", "eric-enmsg-autoprovisioning", "eric-enmsg-cmevents", "eric-enmsg-cmservice", "eric-enmsg-cmutilities", "eric-enmsg-com-ecim-mscm", "eric-enmsg-comecimpolicy", "eric-enmsg-dc-history", "eric-enmsg-domain-proxy-coordinator", "eric-enm-dps-schemamgt", "eric-enmsg-dlms", "eric-enmsg-dpmediation", "eric-enmsg-eventbasedclient", "eric-enmsg-flowautomation", "eric-enmsg-fls", "eric-enm-flsdb-schemamgt", "eric-enmsg-fm-alarm-processing", "eric-enmsg-fm-history", "eric-enmsg-fm-service", "eric-enmsg-general-scripting", "eric-enmsg-gossiprouter-cache", "eric-enmsg-gossiprouter-remoting", "eric-enmsg-gossiprouter-eap7", "eric-enmsg-ha-proxy", "eric-enmsg-import-export-service", "eric-enmsg-ip-service-management", "eric-enmsg-jmsserver", "eric-enmsg-kpi-calc-serv", "eric-enmsg-kpi-service", "eric-enm-kvstore-hc", "eric-enmsg-lcmservice", "eric-enmsg-medrouter", "eric-enm-modeldeployservice", "eric-enm-models", "eric-enmsg-msap", "eric-enmsg-msapgfm", "eric-enmsg-mscm", "eric-enmsg-mscmapg", "eric-enmsg-mscmip", "eric-enmsg-msfm", "eric-enmsg-mskpirt", "eric-enmsg-msnetlog", "eric-enmsg-mspm", "eric-enmsg-mspmip", "eric-enmsg-mssnmpcm", "eric-enmsg-mssnmpfm", "eric-enmsg-nb-alarm-irp-agent-corba", "eric-enmsg-nb-fm-snmp", "eric-enmsg-nbi-bnsi-fm", "eric-enmsg-nedo-serv", "eric-enmsg-networkexplorer", "eric-enmsg-node-plugins", "eric-enmsg-nodecli", "eric-enm-omnidaemon", "eric-enmsg-opendj", "eric-enmsg-openidm", "eric-enmsg-pki-ra-service", "eric-enmsg-pmic-router-policy", "eric-enmsg-pmservice", "eric-enmsg-postgres", "eric-enmsg-sa-service", "eric-enmsg-sentinel", "eric-enm-sfwkdb-schemamgt", "eric-enmsg-shm-core-service", "eric-enmsg-shmservice", "eric-enmsg-smrs-service", "eric-enmsg-solr-service", "eric-enmsg-sps-service", "eric-enmsg-supervisionclient", "eric-enm-troubleshooting-utils", "eric-enmsg-uiservice", "eric-enmsg-visinaming-nb", "eric-enmsg-visinaming-sb", "eric-enmsg-vault-service", "eric-enmsg-web-push-service", "eric-enmsg-elex"]
pipeline {
    agent { node { label 'Cloud-Native' } }
    options {
        timestamps()
    }
    environment {
        PIPELINE_LAST_STAGE_STATUS = 'UNKNOWN'
    }
    parameters {
        string(name: 'product_set_version', description: 'Enter the product set version (e.g. 20.11.52).                      Please ensure to give latest product set version compared to last built product set.', defaultValue: "")
		string(name: 'BRANCH', description: 'Enter the GIT Branch (e.g. master).', defaultValue: "")
    }
    stages {
        stage ('Initialization of ENM Versions') {
            steps {
                script {
                    def productSet = ""
					def branch = ""

                    // For nightly build this parameter would be empty
                    if (params.product_set_version) {
                        productSet = params.product_set_version.trim()
                    }
                    // Check if product set version is given
                    if (productSet != "") {
                        env.PRODUCT_SET = productSet
                        echo "Triggered manually with the PRODUCT_SET: ${productSet}"
                        currentBuild.description = "Triggered Mode: <b>Manual</b><br/>"
                    } else {
                        echo "Triggered automatically."
                        currentBuild.description = "Triggered Mode: <b>Auto</b><br/>"
                    }
                    // Call eric-enm-green-iso-build-trigger with PRODUCT_SET parameter
                    def b = build(job: "eric-enm-green-iso-build-trigger", parameters: [[$class:'StringParameterValue', name:'PRODUCT_SET', value:String.valueOf("${productSet}")]], propagate: true, wait: true)
                    env.ISO_VERSION = "$b.buildVariables.ISO_VERSION"
                    echo "ISO VERSION: $ISO_VERSION"
                    env.PRODUCT_SET = "$b.buildVariables.PRODUCT_SET"
                    echo "PRODUCT SET VERSION: $PRODUCT_SET"
                    env.ENM_ISO_REPO_VERSION = "$b.buildVariables.ENM_ISO_REPO_VERSION"
                    echo "ENM_ISO_REPO_VERSION: $ENM_ISO_REPO_VERSION"
					echo "BRANCH: $BRANCH"

                    // Update the Build Description
                    currentBuild.description += "Product Set Version: $PRODUCT_SET<br/>"
                    currentBuild.description += "ISO Version: $ISO_VERSION<br/>"
                    currentBuild.description += "Yum Repo Setup: $ENM_ISO_REPO_VERSION<br/>"
					currentBuild.description += "GIT Branch: $BRANCH<br/>"

                    environment_list = [[$class:'StringParameterValue', name:"ISO_VERSION", value:String.valueOf("${ISO_VERSION}")],
                                        [$class:'StringParameterValue', name:"ENM_ISO_REPO_VERSION", value:String.valueOf("${ENM_ISO_REPO_VERSION}")],
										[$class:'StringParameterValue', name:"BRANCH", value:String.valueOf("${BRANCH}")]]
                    echo "$environment_list"
                }
            }
        }
        stage ('Building OS Base Images') {
            parallel {
                stage ('Build eric-enm-rhel6base') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_rhel6base_build = build(job: "eric-enm-rhel6base-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_RHEL6BASE_IMAGE_TAG = "$eric_enm_rhel6base_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
                stage ('Build eric-enm-sles-base') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_sles_base_build = build(job: "eric-enm-sles-base-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_SLES_BASE_IMAGE_TAG = "$eric_enm_sles_base_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
                stage('Build eric-oss-monitoring-master') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_oss_monitoring_master_build = build(job: "eric-oss-monitoring-master-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_OSS_MONITORING_MASTER_IMAGE_TAG = "$eric_oss_monitoring_master_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
            }
        }
        stage ('SYNC 1: OS Base Images') {
            steps {
                echo "All OS Base Images up to this point built successfully"
                sleep(5)
                script {
                    build_environment_list()
                }
            }
        }
        stage ('Building Base Images') {
            parallel {
                stage('Build eric-enm-httpd-sidecar') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_httpd_sidecar_build = build(job: "eric-enm-httpd-sidecar-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_HTTPD_SIDECAR_IMAGE_TAG = "$eric_enm_httpd_sidecar_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
                stage ('Build eric-enm-monitoring-master') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_monitoring_master_build = build(job: "eric-enm-monitoring-master-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_MONITORING_MASTER_IMAGE_TAG = "$eric_enm_monitoring_master_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
                stage ('Build eric-enm-monitoring-remotewriter') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_monitoring_remotewriter_build = build(job: "eric-enm-monitoring-remotewriter-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_MONITORING_REMOTEWRITER_IMAGE_TAG = "$eric_enm_monitoring_remotewriter_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
                stage ('Build eric-enm-neo4j-extension-plugin') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_neo4j_extension_plugin_build = build(job: "eric-enm-neo4j-extension-plugin-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_NEO4J_EXTENSION_PLUGIN_IMAGE_TAG = "$eric_enm_neo4j_extension_plugin_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
                stage ('Build eric-enm-rhel6jboss') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_rhel6jboss_build = build(job: "eric-enm-rhel6jboss-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_RHEL6JBOSS_IMAGE_TAG = "$eric_enm_rhel6jboss_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
                stage ('Build eric-enm-init-wait') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_init_wait_build = build(job: "eric-enm-init-wait-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_INIT_CONTAINER_IMAGE_TAG = "$eric_enm_init_wait_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
                stage ('Build eric-enm-sles-eap6') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_sles_eap6_build = build(job: "eric-enm-sles-eap6-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_SLES_EAP6_IMAGE_TAG = "$eric_enm_sles_eap6_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
                stage ('Build eric-enm-sles-eap7') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_sles_eap7_build = build(job: "eric-enm-sles-eap7-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_SLES_EAP7_IMAGE_TAG = "$eric_enm_sles_eap7_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
            }
        }
        stage ('SYNC 2: BaseImages') {
            steps {
                echo "All Base Images up to this point built successfully"
                sleep(5)
                script {
                    build_environment_list()
                }
            }
        }
        stage ('Building more Base Images') {
            parallel {
                stage('Build eric-enm-rhel-jbossconfig') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_rhel_jbossconfig_build = build(job: "eric-enm-rhel-jbossconfig-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_RHEL_JBOSSCONFIG_IMAGE_TAG = "$eric_enm_rhel_jbossconfig_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
                stage ('Build eric-enm-monitoring-eap6') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_monitoring_eap6_build = build(job: "eric-enm-monitoring-eap6-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_MONITORING_EAP6_IMAGE_TAG = "$eric_enm_monitoring_eap6_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
                stage ('Build eric-enm-monitoring-eap7') {
                    options {
                        retry(3)
                    }
                    steps {
                        script {
                            def eric_enm_monitoring_eap7_build = build(job: "eric-enm-monitoring-eap7-pointfix", propagate: true, wait: true, parameters: environment_list)
                            env.ERIC_ENM_MONITORING_EAP7_IMAGE_TAG = "$eric_enm_monitoring_eap7_build.buildVariables.IMAGE_TAG"
                        }
                    }
                }
            }
        }
        stage ('SYNC 3: More BaseImages') {
            steps {
                echo "All Base Images built successfully"
                sleep(5)
                script {
                    build_environment_list()
                }
            }
        }
        stage('Building Service Groups Images and Helm Charts') {
            steps{
                script{
                    def builds = [:]
                    for (def proj in SG_PROJECT_LIST) {
						def service_group_build = "${proj}"+'-pointfix'
                        builds["${service_group_build}"] = {
                            stage("Build ${service_group_build}"){
                                retry(3) {
                                    def image_tag_name = (service_group_build.replaceAll("-", "_") + "_IMAGE_TAG").toUpperCase()
                                    def build = build(job: "${service_group_build}", propagate: true, wait: true, parameters: environment_list)
                                    env."$image_tag_name" = "$build.buildVariables.IMAGE_TAG"
                                    echo "IMAGE TAG: $image_tag_name"
                                }
                            }
                        }
                    }
                    parallel builds
                    echo sh(script: 'env', returnStdout:true)
                }
            }
        }
        stage ('SYNC 4: Service Groups Images and Helms Charts') {
            steps {
                echo "All Service Groups Images and Helms charts built successfully"
                sleep(5)
                script {
                    build_environment_list()
                }
            }
        }
        stage ('Trigger Integration Build') {
            steps {
                script {
                    sh 'echo "ISO_VERSION=$ISO_VERSION" > artifact.properties'
                    sh 'echo "PRODUCT_SET=$PRODUCT_SET" >> artifact.properties'
					sh 'echo "BRANCH=$BRANCH" >> artifact.properties'
                    archiveArtifacts 'artifact.properties'
                }
                sleep(5)
            }
        }
    }
    post {
        success {
            script {
               // sh(script: 'wget -q -O - --no-check-certificate --post-data="" https://ci-portal.seli.wh.rnd.internal.ericsson.com/api/manageCNProductSet/${PRODUCT_SET}/cENM-Build-Images/passed/', returnStdout: true).trim()
                currentBuild.rawBuild.@result = hudson.model.Result.SUCCESS
            }
        }
        failure {
            script {
               // sh(script: 'wget -q -O - --no-check-certificate --post-data="" https://ci-portal.seli.wh.rnd.internal.ericsson.com/api/manageCNProductSet/${PRODUCT_SET}/cENM-Build-Images/failed/', returnStdout: true).trim()
                currentBuild.rawBuild.@result = hudson.model.Result.FAILURE
            }
        }
    }
}
def build_environment_list(){
    env.getEnvironment().each { key, value ->
        if ("${key}".startsWith("ERIC_ENM")){
            echo "${key}=${value}"
            environment_list << [$class:'StringParameterValue', name:"${key}", value:String.valueOf("${value}")]
        }
    }
    build(job: "sync-build-trigger-pointfix", propagate: true, wait: true, parameters: environment_list)
}
