definition.script:
#!/usr/bin/env groovy

/* IMPORTANT:
 *
 * In order to make this pipeline work, the following configuration on Jenkins is required:
 * - slave with a specific label (see pipeline.agent.label below)
 * - credentials plugin should be installed and have the secrets with the following names:
 *   + lciadm100credentials (token to access Artifactory)
 */

def defaultBobImage = 'armdocker.rnd.ericsson.se/proj-am/releases/eric-am-package-manager:2.0.1'

def bob = new BobCommand()
        .bobImage(defaultBobImage)
        .needDockerSocket(true)
        .toString()

def GIT_COMMITTER_NAME = 'lciadm100'
def GIT_COMMITTER_EMAIL = 'lciadm100@ericsson.com'
def failedStage = ''
def csar_package_name = 'enm-installation-package'


pipeline {
    agent {
        label 'CN_CSAR'
    }
    environment {
        HOME_DIR = "${WORKSPACE}"
    }

    stages {
       stage('Checkout Cloud-Native CSAR Git Repository') {
            steps {
                git branch: 'master',
                        url: 'ssh://gerrit.ericsson.se:29418/OSS/com.ericsson.oss.containerisation/eric-enm-csar-package'
            }
        }

        stage('Get cENM integration charts and values file') {
            steps {
                script {
                                env.bro_integration_chart = sh (script: "ls cENM/Definitions/OtherTemplates/ |grep eric-enm-bro-integration*", returnStdout: true ).trim()
                                env.pre_deploy_integration_chart = sh (script: "ls cENM/Definitions/OtherTemplates/ |grep eric-enm-pre-deploy-integration*", returnStdout: true ).trim()
								env.infra_integration_chart = sh (script: "ls cENM/Definitions/OtherTemplates/ |grep eric-enm-infra-integration*", returnStdout: true ).trim()
								env.stateless_integration_chart = sh (script: "ls cENM/Definitions/OtherTemplates/ |grep eric-enm-stateless-integration*", returnStdout: true ).trim()
								env.integration_values_file=sh (script: "ls cENM/Scripts |grep eric-enm-integration-functional-test-values*", returnStdout: true ).trim()
                }
            }
        }
        stage('print files') {
            steps {
                script {
                        sh '''
                                echo ${bro_integration_chart}
								echo ${pre_deploy_integration_chart}
								echo ${infra_integration_chart}
								echo ${stateless_integration_chart}
								echo ${integration_values_file}

                           '''
                }
            }
        }
	}
}

// More about @Builder: http://mrhaki.blogspot.com/2014/05/groovy-goodness-use-builder-ast.html
import groovy.transform.builder.Builder
import groovy.transform.builder.SimpleStrategy

@Builder(builderStrategy = SimpleStrategy, prefix = '')
class BobCommand {
    def bobImage = 'bob.2.0:latest'
    def envVars = [:]
    def needDockerSocket = false

    String toString() {
        def env = envVars
                .collect({ entry -> "-e ${entry.key}=\"${entry.value}\"" })
                .join(' ')
        def cmd = """\
            |docker run
            |--rm
            |--workdir \${PWD}
            |--user \$(id -u):\$(id -g)
            |-v \${PWD}:\${PWD}
            |-v /etc/group:/etc/group:ro
            |-v /etc/passwd:/etc/passwd:ro
            |-v \${HOME}/.m2:\${HOME}/.m2
            |-v \${HOME}/.docker:\${HOME}/.docker
            |${needDockerSocket ? '-v /var/run/docker.sock:/var/run/docker.sock' : ''}
            |${env}
            |\$(for group in \$(id -G); do printf ' --group-add %s' "\$group"; done)
            |${bobImage}
            |"""
        return cmd
                .stripMargin()           // remove indentation
                .replace('\n', ' ')      // join lines
                .replaceAll(/[ ]+/, ' ') // replace multiple spaces by one
    }
}
