    }
}

pipeline {
    agent {
            node
            {
            label slave
            }
        }
    environment{
        repositoryUrl = "https://arm902-eiffel004.athtem.eei.ericsson.se:8443/nexus/content/repositories/releases/"
        CSAR_PACKAGE_NAME = "enm-installation-package"
        OPENIDM = "eric-enm-openidm-change-password:latest"
	    PASSKEY =  "eric-enm-securestorage-regen-passkey"
        OPENIDM_IMAGE_PATH = "armdocker.rnd.ericsson.se/proj-enm"
	    PASSKEY_IMAGE_PATH = "armdocker.rnd.ericsson.se/proj-enm"
	    PACKAGE_TYPE="csar"
    }
    stages {
       stage ('Clean workspace') {
          steps {
                script {
                          sh "docker image prune -fa"
               }
            deleteDir()
         }
       }
        stage( 'Pre Configurations' ) {
            steps {
                 script{
                        sh 'mkdir -p ${PWD}/charts'
                        //sh 'cp settings.xml ${HOME}/.m2/'
                        env.VNF_RELEASE_DATE_TIME = sh(script: "date +'%FT%T%:z'", returnStdout: true).trim()
                        echo "${VNF_RELEASE_DATE_TIME}"
                        env.ENM_RSTATE_NUMBER = sh(script: 'wget -q -O - --no-check-certificate "https://ci-portal.seli.wh.rnd.internal.ericsson.com/getAOMRstate/?product=ENM&drop=${SPRINT_TAG}"', returnStdout: true).trim()
                        echo "${ENM_RSTATE_NUMBER}"
                 }
            }
        }
       stage('Checkout Cloud-Native CSAR Git Repository') {
            steps {
                git branch: 'dTORF-592294',
                        url: 'ssh://gerritmirror-ha.lmera.ericsson.se:29418/OSS/com.ericsson.oss.containerisation/eric-enm-integration-pipeline-code'
            }
        }
        stage('Get Integration Chart'){
            when {
                allOf{
                    environment name: 'BUILD_TYPE', value: 'automatic'
                   }
             }
                steps{
                   script {
                     sh 'mkdir -p ${PWD}/charts'
                     if("${PRODUCT_SET}" > "21.03.57"){
                          sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-bro-integration/eric-enm-bro-integration-${BRO_CHART_VERSION}.tgz -o charts/eric-enm-bro-integration-${BRO_CHART_VERSION}.tgz'
						  sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-pre-deploy-integration/eric-enm-pre-deploy-integration-${PRE_DEPLOY_CHART_VERSION}.tgz -o charts/eric-enm-pre-deploy-integration-${PRE_DEPLOY_CHART_VERSION}.tgz'
                          if("${PRODUCT_SET}" > "21.03.57"){
                          sh 'curl https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-monitoring-integration/eric-enm-monitoring-integration-${MONITORING_CHART_VERSION}.tgz -o charts/eric-enm-monitoring-integration-${MONITORING_CHART_VERSION}.tgz'
                     }
                 }
            }
        }
      }
        stage('integration value version'){
            steps{
                script {
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-integration-values/eric-enm-integration-production-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-production-values-${INTEGRATION_VALUE_VERSION}.yaml'
                     sh 'curl -4 https://arm.epk.ericsson.se/artifactory/proj-enm-dev-internal-helm/eric-enm-integration-values/eric-enm-integration-extra-large-production-values-${INTEGRATION_VALUE_VERSION}.yaml -o scripts/eric-enm-integration-extra-large-production-values-${INTEGRATION_VALUE_VERSION}.yaml'
                      }
                }
        }
        stage('Override integration-values file') {
            steps {
                script {
                    env.integration_values=sh (script: "ls scripts |grep eric-enm-integration-production-values*", returnStdout: true ).trim()
                    overrideIntegrationValues()
                    check_extra_large_values=sh (script: "ls scripts |grep eric-enm-integration-extra-large-production-values*", returnStatus: true )
                    if (check_extra_large_values == 0){
                        env.integration_values=sh (script: "ls scripts |grep eric-enm-integration-extra-large-production-values*", returnStdout: true ).trim()
                        overrideIntegrationValues()
                    }
                    sh "sed -i -z 's/tags:/tags: \\&tags_reference/1' scripts/eric-enm-integration-*"
                    sh "sed -i -z 's/tags:/tags:\\n    *tags_reference/2' scripts/eric-enm-integration-*"
                    sh "sed -i '20,2000{/value_pack_ebs/d}' scripts/eric-enm-integration-*"
                }
            }
        }
        stage('Packaging of Additional images') {
             steps {
                script {
                    sh 'docker pull ${OPENIDM_IMAGE_PATH}/${OPENIDM}'
                    sh 'docker pull ${PASSKEY_IMAGE_PATH}/${PASSKEY}'
                    sh 'docker tag ${OPENIDM_IMAGE_PATH}/${OPENIDM} ${OPENIDM}'
                    sh 'docker tag ${PASSKEY_IMAGE_PATH}/${PASSKEY} ${PASSKEY}'
                    sh 'docker save ${OPENIDM} -o script/eric-enm-openidm-change-password-docker.tar'
                    sh 'docker save ${PASSKEY} -o script/eric-enm-securestorage-regen-passkey-docker.tar'
                    sh 'gzip script/eric-enm-securestorage-regen-passkey-docker.tar'
                    sh 'gzip script/eric-enm-openidm-change-password-docker.tar'
                }
            }
            post {
                always {
                    sh 'docker image remove ${OPENIDM}'
                    sh 'docker image remove ${PASSKEY}'
                    sh 'docker image remove ${OPENIDM_IMAGE_PATH}/${OPENIDM}'
                    sh 'docker image remove ${PASSKEY_IMAGE_PATH}/${PASSKEY}'
                }
            }
        }
        stage('Generate new version') {
            steps {
            sh "${bob} generate-new-version"
                script {
                    env.VERSION = sh(script: "cat .bob/var.version", returnStdout:true).trim()
                    echo "${VERSION}"
                    env.VNF_PACKAGE_VERSION = sh(script: "echo CXP9039612_$VERSION", returnStdout:true).trim()
                    echo "$VNF_PACKAGE_VERSION"
                }
            }
        }
        stage('Update manifest details') {
            steps {
                script {
                    env.NOW = sh(script: "date +'%FT%TZ'", returnStdout:true).trim()
                    echo "${NOW}"
                    sh "sed -i -e '/^vnf_package_version:/s/.*/vnf_package_version: $VNF_PACKAGE_VERSION/' -e '/^vnf_release_date_time:/s/.*/vnf_release_date_time: $NOW/' manifest/enm_descriptor.mf"
                    sh("cat manifest/enm_descriptor.mf")
                }
            }
        }
        stage('Update VNFD details') {
            steps {
                script {
                    sh "mkdir -p vnfd_updated"
                    sh "cp vnfd/enm_descriptor.yaml vnfd_updated/enm_descriptor.yaml"
                    sh "sed -i 's/<<VERSION>>/$VERSION/g' vnfd_updated/enm_descriptor.yaml"
                    sh "sed -i 's/<<DESCRIPTOR_ID>>/${UUID.randomUUID().toString()}/g' vnfd_updated/enm_descriptor.yaml"
                    sh "sed -i 's/<<PRODUCT_SET>>/${PRODUCT_SET}/g' vnfd_updated/enm_descriptor.yaml"
					sh "sed -i 's/<<BRO_CHART_VERSION>>/${BRO_CHART_VERSION}/g' vnfd_updated/enm_descriptor.yaml"
                    sh "sed -i 's/<<PRE_DEPLOY_CHART_VERSION>>/${PRE_DEPLOY_CHART_VERSION}/g' vnfd_updated/enm_descriptor.yaml"
                    if("${PRODUCT_SET}" > "21.03.57"){
                        sh "sed -i 's/<<MONITORING_CHART_VERSION>>/${MONITORING_CHART_VERSION}/g' vnfd_updated/enm_descriptor.yaml"
                    }
                }
            }
        }
        stage('Build CSAR Package') {
            steps {
                script {
                        def AMPackageWithCsar = new AMPackageCommand()
                                .AMPackageImage(defaultAMPackageImage)
                                .needDockerSocket(true)
                                .envVars(['ENM_INSTALLATION_CHART_VERSION': env.INFRA_CHART_VERSION,
                                          'INTEGRATION_VALUE_VERSION': env.INTEGRATION_VALUE_VERSION,
                                ])
                                .toString()
                        sh "${AMPackageWithCsar} generate -hd charts/ --name ${CSAR_PACKAGE_NAME}-${env.VERSION} -sc scripts --set global.restore.state=ongoing -mf manifest/enm_descriptor.mf --history history/ChangeLog.txt --vnfd vnfd_updated/enm_descriptor.yaml --definitions definitions/etsi_nfv_sol001_vnfd_2_5_1_types.yaml"
                }
            }
        }
        stage('Generate CSAR Parameters') {
            steps {
                script {
                            sh 'echo "CSAR_PACKAGE_VERSION=${VERSION}" > artifact.properties'
							sh 'echo "BRO_CHART_VERSION=${BRO_CHART_VERSION}" >> artifact.properties'
                            sh 'echo "PRE_DEPLOY_VERSION=${PRE_DEPLOY_CHART_VERSION}" >> artifact.properties'
                            if("${PRODUCT_SET}" > "21.03.57"){
                                 sh 'echo "MONITORING_CHART_VERSION=${MONITORING_CHART_VERSION}" >> artifact.properties'
                            }
                            sh 'echo "INTEGRATION_VALUE_VERSION=${INTEGRATION_VALUE_VERSION}" >> artifact.properties'
                            archiveArtifacts 'artifact.properties'
                }
            }
        }

    }
    post {
        success {
              script {
                   sh 'echo "testing success"'
            }
        }
        failure {
            mail to: 'teja.balla1@tcs.com',
            subject: "Failed Pipeline: ${currentBuild.fullDisplayName}",
            body: "Failure on ${env.BUILD_URL}"
        }
    }
}

// More about @Builder: http://mrhaki.blogspot.com/2014/05/groovy-goodness-use-builder-ast.html
import groovy.transform.builder.Builder
import groovy.transform.builder.SimpleStrategy

@Builder(builderStrategy = SimpleStrategy, prefix = '')
class AMPackageCommand {
    def AMPackageImage = 'AMPackage.2.0:latest'
    def envVars = [:]
    def needDockerSocket = false

    String toString() {
        def env = envVars
                .collect({ entry -> "-e ${entry.key}=\"${entry.value}\"" })
                .join(' ')
        def cmd = """\
            |docker run
            |--rm
            |--workdir \${PWD}
            |--user \$(id -u):\$(id -g)
            |-v \${PWD}:\${PWD}
            |-v /etc/group:/etc/group:ro
            |-v /etc/passwd:/etc/passwd:ro
            |-v \${HOME}/.m2:\${HOME}/.m2
            |-v \${HOME}/.docker:\${HOME}/.docker
            |${needDockerSocket ? '-v /var/run/docker.sock:/var/run/docker.sock' : ''}
            |${env}
            |\$(for group in \$(id -G); do printf ' --group-add %s' "\$group"; done)
            |${AMPackageImage}
            |"""
        return cmd
                .stripMargin()           // remove indentation
                .replace('\n', ' ')      // join lines
                .replaceAll(/[ ]+/, ' ') // replace multiple spaces by one
    }
}

@Builder(builderStrategy = SimpleStrategy, prefix = '')
class BobCommand {
    def bobImage = 'bob.2.0:1.5.2-0'
    def envVars = [:]
    def needDockerSocket = false
    String toString() {
        def env = envVars
                .collect({ entry -> "-e ${entry.key}=\"${entry.value}\"" })
                .join(' ')
        def cmd = """\
            |docker run
            |--rm
            |--workdir \${PWD}
            |--user \$(id -u):\$(id -g)
            |-v \${PWD}:\${PWD}
            |-v /etc/group:/etc/group:ro
            |-v /etc/passwd:/etc/passwd:ro
            |-v \${HOME}/.m2:\${HOME}/.m2
            |-v \${HOME}/.docker:\${HOME}/.docker
            |${needDockerSocket ? '-v /var/run/docker.sock:/var/run/docker.sock' : ''}
            |${env}
            |\$(for group in \$(id -G); do printf ' --group-add %s' \$group; done)
            |${bobImage}
            |"""
        return cmd
                .stripMargin()           // remove indentation
                .replace('\n', ' ')      // join lines
                .replaceAll(/[ ]+/, ' ') // replace multiple spaces by one
    }
}
