definition.script:
pipeline {
    agent {
        label 'Cloud-Native'
    }
    stages {
        stage('Checkout Cloud-Native SG Git Repository') {
            steps {
                git branch: 'master',
                        url: 'ssh://gerrit.ericsson.se:29418/OSS/com.ericsson.oss.containerisation/eric-enmsg-access-control'
            }
        }
        stage('Remove Docker images') {
            steps {
                script {
                    def DOCKER_FILE_SIDECAR = sh(script: "find . -name \"Dockerfile*\" | grep \"httpd\" | head -1", returnStdout: true)
                    if (DOCKER_FILE_SIDECAR) {
                        echo "exists"
                    } else {
                    echo "not exists"
                    }
                }
            }
        }
    }
}
