definition.script:
def SG_PROJECT_LIST = ["test12", "test123"]

pipeline {
    agent any

    stages {
        stage('Initialize') {
            steps {
                script {
                  for (def proj in SG_PROJECT_LIST) {
                    stage("Build ${proj}") {
                       def result=build(job: "${proj}", propagate: false, wait: true)

					   def linuxTest = result.getResult()
					   echo "linuxTest===$linuxTest"
					   //stageResult."{proj}" = "$linuxTest"
                       if (linuxTest != "SUCCESS") {
                          currentBuild.result = 'FAILURE'
						  sh "echo testt-----${proj}"
						  sh "echo ${proj} >> artifact.properties"
                       }
                    }
                  }
                }
            }
        }
        stage('Generate Parameters') {
            steps {
                script {

                            archiveArtifacts 'artifact.properties'
                            sh "rm -rf artifact.properties"
                }
            }
        }
    }
}
